mapper.insert:
INSERT INTO ARTICLE_CONTENT
    (PK_ID, ARTICLE_ID, DRAFT, CONTENT, CREATE_TIME)
VALUES (#{pkId}, #{articleId}, #{draft}, #{content}, #{createTime});

mapper.delete:
DELETE FROM ARTICLE_CONTENT
WHERE ARTICLE_ID = #{articleId}

    AND DRAFT = '1'

mapper.update:
UPDATE ARTICLE
SET TITLE       = #{title},
    UPDATE_TIME = #{updateTime},
    PUBLIC_FLAG = #{publicFlag}
WHERE PK_ID = #{pkId}

UPDATE ARTICLE_CONTENT
SET content = #{content}
WHERE ARTICLE_ID = #{articleId}
  AND DRAFT = #{draft}

UPDATE ARTICLE
SET IS_DEL = '1'
WHERE PK_ID = #{articleId}
